import React, { useState, useRef, useEffect } from "react";
import { GoogleGenerativeAI } from "@google/generative-ai";
import "../styles/chatbot.css";

const Chatbot = () => {
  const [input, setInput] = useState("");
  const [chatHistories, setChatHistories] = useState({});
  const [isTyping, setIsTyping] = useState(false);
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const messagesEndRef = useRef(null);

  const characters = {
    captain: {
      name: "Captain Cash",
      image: "data:image/jpeg;base64,",
      greeting: "Ahoy there! I'm Captain Cash! Want to learn about treasure (money)?",
      style: "pirate"
    },
    doraemon: {
      name: "Doraemon",
      image: "https://freepngimg.com/thumb/doraemon/71299-nobi-youtube-nobita-television-doraemon-free-photo-png-thumb.png",
      greeting: "Konnichiwa! I'm Doraemon with my financial gadgets from the 22nd century! Let me help you learn about money!",
      style: "doraemon",
      emoji: "üê±" ,
      
      
    },
    jethalal: {
      name: "Jethalal Gada",
      image: "data:image/jpeg;base64,",
      greeting: "Are bapre bap! Mera naam Jethalal Gada hai! Chalo aaj hum paiso ke baare mein seekhenge! Babita ji bhi kehti hai paisa important hai!",
      style: "jethalal"
    },
    joker: {
      name: "Jocker",
      image: "data:image/jpeg;base64,",
      greeting: "Why so serious about money? Hahaha! Mr. J here to make finance FUN! Let's put a smile on that bank account!",
      style: "joker"
    },
    spiderman: {
      name: "Spider-Man",
      image: "https://freepngimg.com/thumb/spiderman/104514-spider-man-miles-morales-free-clipart-hd-thumb.png",
      greeting: "Hey there! Your friendly neighborhood Spider-Man here! With great money comes great responsibility! Need financial advice?",
      style: "spiderman"
    },
    krishna: {
      name: "Lord Krishna",
      image: "data:image/jpeg;base64,",
      greeting: "Radhe Radhe! I am Krishna, here to guide you on the righteous path of wealth. Let me share the wisdom of Artha (wealth) with you.",
      style: "krishna"
    },
    hermione: {
      name: "Hermione Granger",
      image: "data:image/jpeg;base64,",
      greeting: "Hello! I'm Hermione Granger, and saving money is just as important as studying! Did you know the Goblins run Gringotts Bank?",
      style: "hermione"
    },
    baburao: {
        name: "Babu Bhaiya",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSnuhn-PeF4G7AkXxWXXjoJvslSpPI8TRJPdg&s", // Use your own hosted image or upload to i.ibb.co
        greeting: "Arre Raju! Paisa double nahi hota... samajh ke kharch karo! Main hoon Babu Bhaiya, sikhata hoon paise ka sahi hisaab!",
        style: "baburao"
      },
      naruto: {
        name: "Naruto Uzumaki",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSyAECyTAZmuc1DGvCadi8LDVZfTuAyJPOMYA&s", // Use your own hosted image or upload to i.ibb.co
        greeting: "Dattebayo! I'm Naruto Uzumaki! Even ninjas need to budget their chakra... and their money! Let‚Äôs reach Hokage-level savings!",
        style: "naruto"
      }
  };
  

  const styleGuides = {
    pirate: `You're Captain Cash, a cheerful pirate who teaches money concepts. Use pirate slang like "Arrr!", "Ahoy!", "Matey!", and "Shiver me timbers!" Refer to money as "booty", "gold", or "treasure". Speak in short, catchy sentences like sea shanties. End with sea-themed emojis üè¥‚Äç‚ò†Ô∏èüí∞‚öì.`,
  
    doraemon: `You're Doraemon, a futuristic robot cat from Japan. Use Japanese expressions like "Konnichiwa!", "Yatta!", and mention your gadgets like the "Anywhere Door" or "Time Cloth". Explain with clarity, childlike wonder, and excitement. Add cat or gadget emojis üê±üîßüï∞Ô∏è.`,
  
    jethalal: `You're Jethalal Gada from TMKOC. Talk in dramatic Hinglish, full of confusion and sass. Use catchphrases like "Arey bapuji!", "Babita ji!", "Aiyo Mehta Sahab!", and "Tapu ke papa bolta hai...". Be overly emotional about simple things like budgeting and savings. Add some Gujarati flavor‚Äîsay things like "Kem cho?" or "Bakaasur mathu khava no!". Explain financial tips with Gokuldham-style chaos and humor. Wrap up with exaggerated reactions and TMKOC flair üòÇüì∫ü•¥.`,
  
    joker: `You're the Joker from DC Comics, but family-friendly. Speak with a mix of chaos and charm. Use phrases like "Why so serious?", "Let‚Äôs put a smile on that bank account!", and playful mischief. Talk in metaphors related to clowns, cards, chaos. Insert dark but humorous emojis üé≠üÉèüíµ.`,
  
    spiderman: `You're Peter Parker a.k.a Spider-Man. Use teenage-friendly language with excitement. Say phrases like "With great power comes great responsibility", "Web-slinging through your budget", and "Your friendly neighborhood financial helper". Add web/power themed emojis üï∑Ô∏èüï∏Ô∏èüí°.`,
  
    krishna: `You are Lord Krishna, full of divine wisdom and compassion. Speak in a calm, poetic manner. Refer to Artha (wealth) and Dharma (duty). Use Sanskrit wisdom like "Yogah Karmasu Kaushalam" and analogies from Mahabharata. Begin with "My dear child" or "Radhe Radhe". Add flute, peacock, and divine emojis ü™∂üé∂üïâÔ∏è.`,
  
    hermione: `You're Hermione Granger from Harry Potter. Speak with intelligence, precision, and a touch of British politeness. Say things like "Honestly, basic savings is just common sense!" or "You must be as smart as a Ravenclaw". Use magical financial references like "Budgetus Maximus!" or "Compound Interest Charm". Use book and magic emojis üìö‚ú®üßô‚Äç‚ôÄÔ∏è.`,
  
    naruto: `You're Naruto Uzumaki, the ninja who never gives up! Speak with passion and energy. Use phrases like "Believe it!", "Dattebayo!", and talk about achieving financial goals like becoming the Hokage of budgeting. Refer to ninja training for saving money and missions for investments. Use ninja and chakra-themed emojis üçúü•∑üî•üçÉ.`,
  
    baburao: `You're Baburao Ganpatrao Apte from Hera Pheri. Speak in his iconic confused-yet-funny tone. Use phrases like "Utha le re baba!", "Mere ko to aisa dhak dhak horela hai!", and mispronounce finance terms humorously. Be silly but give practical advice in the most hilarious way. Refer to phone calls as "wrong number" if it's a scam. End with classic Baburao-style punchlines and chaos üòÇ‚òéÔ∏èü•¥üí∏.`
  };
  
  
  // Get current messages
  const messages = chatHistories[selectedCharacter] || [];

  // Initialize greeting on character selection
  useEffect(() => {
    if (selectedCharacter && !chatHistories[selectedCharacter]) {
      setChatHistories(prev => ({
        ...prev,
        [selectedCharacter]: [{
          text: `${characters[selectedCharacter].greeting || "Hello!"}`,
          sender: "bot"
        }]
      }));
    }
    setInput(""); // Clear input on character switch
  }, [selectedCharacter]);

  // Scroll to bottom on message change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Initialize Gemini
  const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

  const handleSend = async () => {
    if (!input.trim() || !selectedCharacter) return;

    // Add user message
    setChatHistories(prev => ({
      ...prev,
      [selectedCharacter]: [
        ...(prev[selectedCharacter] || []),
        { text: input, sender: "user" }
      ]
    }));

    const userInput = input;
    setInput("");
    setIsTyping(true);

    try {
      const model = genAI.getGenerativeModel({
        model: "gemini-1.5-flash-latest",
        generationConfig: {
          temperature: 0.9,
          topP: 0.95,
        },
      });

      let characterPrompt = `Explain "${userInput}" to a 10-year-old in the style of ${characters[selectedCharacter].name}.
      ${styleGuides[characters[selectedCharacter].style] || ""}
      Use simple words, emojis, and keep it under 3 sentences.
      You are ${characters[selectedCharacter].name} with personality: ${characters[selectedCharacter].emoji}.`;

      if (selectedCharacter === 'jethalal') {
        characterPrompt += " Respond in Hindi-English mix (Hinglish) using Devanagari script mixed with English.";
      }

      const result = await model.generateContent(characterPrompt);
      const response = await result.response;
      const text = response.text();

      setChatHistories(prev => ({
        ...prev,
        [selectedCharacter]: [
          ...(prev[selectedCharacter] || []),
          { text, sender: "bot" }
        ]
      }));
    } catch (error) {
      console.error("Gemini error:", error);
      const fallback = error.message.includes("API_KEY")
        ? "üîë Psst! You forgot the treasure map (API key)!"
        : "‚ö° Oh no! My magic is malfunctioning. Try again later!";
      setChatHistories(prev => ({
        ...prev,
        [selectedCharacter]: [
          ...(prev[selectedCharacter] || []),
          { text: fallback, sender: "bot" }
        ]
      }));
    } finally {
      setIsTyping(false);
    }
  };

  if (!selectedCharacter) {
    return (
      <div className="character-selection">
        <h2>Choose Your Financial Guide</h2>
        <div className="character-options">
          {Object.entries(characters).map(([key, character]) => (
            <div
              key={key}
              className={`character-card ${key}`}
              onClick={() => setSelectedCharacter(key)}
            >
              <div className="character-emoji">
  <img src={character.image} alt={character.name} />
</div>

              <h3>{character.name}</h3>
              <p>{character.greeting}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className={`chatbot-container ${selectedCharacter}`}>
      <div className="chatbot-header">
        <h3>
          {characters[selectedCharacter].emoji} {characters[selectedCharacter].name}
          <button
            className="change-character"
            onClick={() => setSelectedCharacter(null)}
          >
            Change Character
          </button>
        </h3>
      </div>
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        {isTyping && (
          <div className="message bot">
            <span className="typing-dots">...</span>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder={`Ask ${characters[selectedCharacter].name} about money...`}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
